mapper.insert:
INSERT INTO t_emp_ledger /* EmpLedgerDao.insert */ ( emp_id, emp_pool_id, company, department, assign_date, end_date, sales_mm, purchase_mm, sales_unit, purchase_unit, comments, resume_submit_date, resign_date, i_contract_date, c_contract_date, progress, progress_reason, issues, del, site, client, project_name, brief, system_creator, system_modifier, system_created_at, system_modified_at ) VALUES ( DEFAULT, #{emp_pool_id}, #{company}, #{department}, #{assign_date}, #{end_date}, #{sales_mm}, #{purchase_mm}, #{sales_unit}, #{purchase_unit}, #{comments}, #{resume_submit_date}, #{resign_date}, #{i_contract_date}, #{c_contract_date}, #{progress}, #{progress_reason}, #{issues}, 'N', #{site}, #{client}, #{project_name}, #{brief}, #{system_creator}, #{system_modifier}, now(), now()
)
SELECT currval('t_emp_ledger_emp_id_seq')

mapper.select:
SELECT /* EmpLedgerDao.selectProjectYn */
	CASE 
		WHEN count(*) >= 1 THEN 'Y'
		ELSE 'N'
	END AS result
FROM t_emp_ledger
WHERE emp_pool_id = #{emp_pool_id} and progress in ('투입예정', '투입')
	AND company = #{company}
	AND department = #{department}
	AND site = #{site}
	AND client = #{client}
	AND project_name = #{project_name}
	AND (del = 'N' or del is null);

SELECT /* EmpLedgerDao.getMonthlyMM */ t2.emp_id, ( SELECT CASE WHEN to_char(t2.assign_date, 'YYYYMM') > #{batch_month} THEN TRUNC(0, 3) WHEN to_char(t2.assign_date, 'YYYYMM') = #{batch_month} THEN 1 - TRUNC( (EXTRACT(DAY FROM t2.assign_date) - 1) / EXTRACT(DAY FROM (DATE_TRUNC('month', TO_DATE(#{batch_month}, 'YYYYMM')) + INTERVAL '1 month' - INTERVAL '1 day')) , 3) WHEN to_char(t2.resign_date, 'YYYYMM') = #{batch_month} THEN TRUNC( EXTRACT(DAY FROM t2.resign_date) / EXTRACT(DAY FROM (DATE_TRUNC('month', TO_DATE(#{batch_month}, 'YYYYMM')) + INTERVAL '1 month' - INTERVAL '1 day')) , 3) WHEN to_char(t2.resign_date, 'YYYYMM') < #{batch_month} THEN TRUNC(0, 3) ELSE TRUNC(1, 3) END
) 자동계산mm
FROM t_sales_ledger t1 INNER JOIN t_emp_ledger t2 on t1.emp_id = t2.emp_id INNER JOIN t_emp_pool t3 on t2.emp_pool_id = t3.emp_pool_id WHERE ( t1.del != 'Y' OR t1.del IS NULL )
AND t2.company = #{company}
AND t2.department = #{department}
